import { Alchemy, Network } from "alchemy-sdk";
import { useEffect, useState } from "react";

import "./App.css";

// Refer to the README doc for more information about using API
// keys in client-side code. You should never do this in production
// level code.
const settings = {
  apiKey: process.env.REACT_APP_ALCHEMY_API_KEY,
  network: Network.ETH_MAINNET,
};

// In this week's lessons we used ethers.js. Here we are using the
// Alchemy SDK is an umbrella library with several different packages.
//
// You can read more about the packages here:
//   https://docs.alchemy.com/reference/alchemy-sdk-api-surface-overview#api-surface
const alchemy = new Alchemy(settings);

function App() {
  useEffect(() => {
    const [blockNumber, setBlockNumber] = useState("");
    const [blockInfo, setBlockInfo] = useState("");
    async function getBlockNumber() {
      try {
        setBlockNumber(await alchemy.core.getBlockNumber());
      } catch {
        console.log("mistake");
      }
    }

    async function getBlockInfo(block_Number) {
      try {
        setBlockInfo(await alchemy.core.getBlock(block_Number));
      } catch {
        console.log("mistake 2");
      }
    }

    getBlockNumber();
    getBlockInfo(blockNumber);

    // console.log(parseInt(blockInfo.gasUsed._hex));
  }, []);

  function getTime(timestamp) {
    let unix_timestamp = timestamp;
    // Create a new JavaScript Date object based on the timestamp
    // multiplied by 1000 so that the argument is in milliseconds, not seconds.
    var date = new Date(unix_timestamp * 1000);
    // Hours part from the timestamp
    var hours = date.getHours();
    // Minutes part from the timestamp
    var minutes = "0" + date.getMinutes();
    // Seconds part from the timestamp
    var seconds = "0" + date.getSeconds();
    // Will display time in 10:30:23 format
    var formattedTime = hours + ":" + minutes + ":" + seconds.slice(1, 3);
    return formattedTime;
  }

  let time = getTime(blockInfo.timestamp);
  let txs = blockInfo.transactions.length;
  let gas = parseInt(blockInfo.gasUsed._hex);

  return (
    <div className="App">
      <div>Block Number: {blockNumber}</div>
      <div>Block Timestamp: {time}</div>
      <div>Amount of Transactions: {txs}</div>
      <div>Gas: {gas}</div>
    </div>
  );
}

export default App;
