import { Alchemy, Network } from "alchemy-sdk";
import { useState } from "react";

export const Accounts = () => {
  const settings = {
    apiKey: process.env.REACT_APP_ALCHEMY_API_KEY,
    network: Network.ETH_MAINNET,
  };

  const alchemy = new Alchemy(settings);

  let [balance, setBalance] = useState(0);
  let [userAddress, setUserAddress] = useState("");
  let [showBalance, setShowBalance] = useState(false);

  async function getBalance(address) {
    try {
      let response = await alchemy.core.getBalance(address, "latest");
      setBalance(Utils.formatEther(parseInt(response.value._hex).toString()));
      setShowBalance(!showBalance);
    } catch {
      alert("This address doesn't exist.");
    }
  }
  return (
    <div>
      <input
        type="text"
        placeholder="0x..."
        onChange={(e) => {
          setUserAddress(e.target.value);
        }}
      />
      <button
        onClick={() => {
          getBalance(userAddress);
        }}
      >
        Get Balance
      </button>
      {showBalance ? balance : <div></div>}
    </div>
  );
};

export default Accounts;
