import { Alchemy, Network } from "alchemy-sdk";
import { useState } from "react";
import MoreInfoTx from "./MoreInfoTx";

export const Transactions = (props) => {
  let i = 0;

  const settings = {
    apiKey: process.env.REACT_APP_ALCHEMY_API_KEY,
    network: Network.ETH_MAINNET,
  };

  const alchemy = new Alchemy(settings);

  let [from, setFrom] = useState("");
  let [to, setTo] = useState("");
  let [amount, setAmount] = useState(0);

  async function getMoreInfo(hash) {
    let response = await alchemy.core.getTransaction(hash);
    setFrom(response.from);
    setTo(response.to);
    setAmount(parseInt(response.value._hex));
  }

  return (
    <div>
      <h1>List of Txs:</h1>
      {props.txs.map((el, i) => {
        return (
          <ul key={i}>
            {el}{" "}
            <button
              onClick={() => {
                getMoreInfo(el);
                return <MoreInfoTx from={from} to={to} amount={amount} />;
              }}
            >
              More Info
            </button>
          </ul>
        );
      })}
    </div>
  );
};

export default Transactions;
